import React, { Component } from 'react';
import './Burger.css'

class Burger extends Component {
  constructor(props){
    super(props);
    this.state={
      cheese : 0,
      lettuce : 0,
      meat : 0,
      tomato : 0,
      totalprice : 0
    }
  }

  addingredient=(ingredient, price)=>{
    let orig;
    let pri = this.state.totalprice
    switch(ingredient) {
      case "cheese":
        orig = this.state.cheese;
        this.setState({cheese: orig+1});
        this.setState({totalprice: pri + price});
        break;
      case "lettuce":
        orig = this.state.lettuce;
        this.setState({lettuce: orig+1});
        this.setState({totalprice: pri + price});
        break;
      case "meat":
        orig = this.state.meat;
        this.setState({meat: orig+1});
        this.setState({totalprice: pri + price});
        break;
      case "tomato":
        orig = this.state.tomato;
        this.setState({tomato: orig+1});
        this.setState({totalprice: pri + price});
        break;
      default:
        break;
    }
  }

  render() {
    return (
      <div className = "container" >
        <div className = "burgerbuilder">
        <h1 className = "lheading">Burger Builder</h1>
        </div>

        <div className = "burgermenu">
          <h1 className = "rheading">Burger Menu</h1>
          <div  className = "menucontainer">
            <div className = "item">
              <h2>Total Price: {this.state.totalprice}</h2>
            </div>
            <div className = "item">
              <h2 className= "lheading">Cheese</h2>
              <h2>{this.state.cheese}</h2>
              <button onClick={()=>this.addingredient("cheese", 2)}>ADD</button>
              <button >REMOVE</button>
            </div>
            <div className = "item">
              <h2 className= "lheading">Lettuce</h2>
              <h2>{this.state.lettuce}</h2>
              <button onClick={()=>this.addingredient("lettuce", 1)}>ADD</button>
              <button >REMOVE</button>
            </div>
            <div className = "item">
              <h2 className= "lheading">Tomato</h2>
              <h2>{this.state.tomato}</h2>
              <button onClick={()=>this.addingredient("tomato", 1)}>ADD</button>
              <button >REMOVE</button>
            </div>
            <div className = "item">
              <h2 className= "lheading">Meat</h2>
              <h2>{this.state.meat}</h2>
              <button onClick={()=>this.addingredient("meat", 3)}>ADD</button>
              <button >REMOVE</button>
            </div>
          </div>
        </div>

      </div>
    );
  }
}

export default Burger;
